import { useState, useEffect } from 'react'

const emptyForm = {
  name: '',
  type: '',
  status: '',
  lastCleaned: ''
}

export default function EquipmentForm({ onSubmit, editing }) {
  const [form, setForm] = useState(emptyForm)

  useEffect(() => {
    if (editing) setForm(editing)
  }, [editing])

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  const handleSubmit = e => {
    e.preventDefault()
    if (!form.name || !form.type || !form.status) {
      alert('All fields required')
      return
    }
    onSubmit(form)
    setForm(emptyForm)
  }

  return (
   <div className="bg-white shadow-lg rounded-xl p-6 mb-8 max-w-4xl">
  <h2 className="text-xl font-semibold text-gray-700 mb-4">
    Add Equipment
  </h2>

  <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-5 gap-4 items-end">

    <input
      name="name"
      placeholder="Equipment Name"
      value={form.name}
      onChange={handleChange}
      className="border rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 outline-none"
    />

    <select
      name="type"
      value={form.type}
      onChange={handleChange}
      className="border rounded-lg px-3 py-2 bg-white focus:ring-2 focus:ring-blue-500 outline-none"
    >
      <option value="">Select Type</option>
      <option>Machine</option>
      <option>Vessel</option>
      <option>Tank</option>
      <option>Mixer</option>
    </select>

    <select
      name="status"
      value={form.status}
      onChange={handleChange}
      className="border rounded-lg px-3 py-2 bg-white focus:ring-2 focus:ring-blue-500 outline-none"
    >
      <option value="">Select Status</option>
      <option>Active</option>
      <option>Inactive</option>
      <option>Under Maintenance</option>
    </select>

    <input
      type="date"
      name="lastCleaned"
      value={form.lastCleaned}
      onChange={handleChange}
      className="border rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 outline-none"
    />

    <button
      type="submit"
      className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-4 py-2 rounded-lg transition"
    >
      Save
    </button>
  </form>
</div>

  )
}
