import { useState, useEffect } from 'react'

const emptyForm = {
  name: '',
  type: '',
  status: '',
  lastCleaned: ''
}

export default function EquipmentForm({ onSubmit, editing }) {
  const [form, setForm] = useState(emptyForm)

  useEffect(() => {
    if (editing) setForm(editing)
  }, [editing])

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  const handleSubmit = e => {
    e.preventDefault()
    if (!form.name || !form.type || !form.status) {
      alert('All fields required')
      return
    }
    onSubmit(form)
    setForm(emptyForm)
  }

  return (
    <div className="max-w-md mx-auto bg-white shadow-xl rounded-2xl p-6 mt-8">
      <h3 className="text-2xl font-semibold text-gray-800 mb-6 text-center">
        {editing ? 'Edit Equipment' : 'Add Equipment'}
      </h3>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="name"
          placeholder="Equipment Name"
          value={form.name}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-500 outline-none"
        />

        <select
          name="type"
          value={form.type}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-lg px-4 py-2 bg-white focus:ring-2 focus:ring-blue-500 outline-none"
        >
          <option value="">Select Type</option>
          <option>Machine</option>
          <option>Vessel</option>
          <option>Tank</option>
          <option>Mixer</option>
        </select>

        <select
          name="status"
          value={form.status}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-lg px-4 py-2 bg-white focus:ring-2 focus:ring-blue-500 outline-none"
        >
          <option value="">Select Status</option>
          <option>Active</option>
          <option>Inactive</option>
          <option>Under Maintenance</option>
        </select>

        <input
          type="date"
          name="lastCleaned"
          value={form.lastCleaned}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-500 outline-none"
        />

        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 rounded-lg transition duration-200"
        >
          Save Equipment
        </button>
      </form>
    </div>
  )
}
